import React from 'react'
import { MessageData } from '../models/message.model'
import styled from 'styled-components';

interface StyledProps {
    me?: boolean;
}

const MessageBox = styled.li<StyledProps>`
    /* text-align: ${props => props.me ? "left" : "right"}; */
    background-color: lightblue;
    margin: 1rem 0;
    padding: 5px;
    border-radius:10px;
    width: fit-content;
`
const MessageContent = styled.div`
    display: flex;
    gap: 5px;
`
const Username = styled.h4`
    padding: 0;
    margin: 0;
    font-weight: 600;
    color: magenta;
    margin-left:5px;
`
const Text = styled.h4`
    padding: 0;
    margin: 0;
    font-weight: 400;
    margin-right:5px;
`

type Props = {
    message: MessageData;
    currentMember: string;
}

function Message({ message, currentMember }: Props): JSX.Element {
    return (
        <MessageBox me={message.member === currentMember}>
            <MessageContent me={message.member === currentMember}>
                <Username >{message.member}:</Username>
                <Text >{message.text}</Text>
            </MessageContent>
        </MessageBox>
    )
}

export default Message
