import React from 'react'
import { MessageData } from '../models/message.model'
import styled from 'styled-components';
import { start } from 'repl';

interface StyledProps {
    me?: boolean;
}

const MessageBox = styled.li`
    background-color: lightblue;
    margin: 1rem 0;
    padding: 5px;
    border-radius:10px;
    width: fit-content;
    text-align:end
`
const MessageContent = styled.div<StyledProps>`
    text-align: ${props => props.me ? left : end};
    display: flex;
    gap: 5px;
`
const Username = styled.h4`
    padding: 0;
    margin: 0;
    font-weight: 600;
    color: magenta;
    margin-left:5px;
`
const Text = styled.h4`
    padding: 0;
    margin: 0;
    font-weight: 400;
    margin-right:5px;
`

type Props = {
    message: MessageData;
    currentMember: string;
}

function Message({ message, currentMember }: Props): JSX.Element {
    return (
        <MessageBox >
            <MessageContent>
                {/* {(message.member === currentMember) ?
                    <Username me>{message.member}:</Username> :
                    <Username  >{message.member}:</Username>
                } */}
                <Username me={message.member === currentMember} >{message.member}:</Username>
                <Text >{message.text}</Text>
            </MessageContent>
        </MessageBox>
    )
}

export default Message
