import React, { useState } from 'react'

type Props = {}

function MessageForm({ }: Props) {
    const [text, settext] = useState("");

    const onChange = e => {
        settext(e.target.value);
    }

    return (
        <form>
            <input type="text" name="text" value={text} onChange={onChange} />
            <button>Send</button>
        </form>
    )
}

export default MessageForm
