import React from 'react'
import { MessageData } from '../models/message.model'
import styled from 'styled-components';

const MessageBox = styled.li`
    background-color: lightblue;
    margin: 1rem 0;
    padding: 15px;
`
const MessageContent = styled.div`
    text-align:start;
    display: flex;
    flex-direction:column;
`

type Props = {
    message: MessageData
}

function Message({ message }: Props): JSX.Element {
    return (
        <MessageBox >
            <MessageContent>
                <div className="username">{message.member} :</div>
                <div className="text">{message.text}</div>
            </MessageContent>
        </MessageBox>
    )
}

export default Message
